'use strict';

var Util = require('../util.js');

exports = module.exports = function(){
	return exports;
}
	
exports.handle = function(node, tplPath, response, starttime){
	
	Util.traverseNodeTree(node, response, function(textNode, response){
		//send to response
		response.write(textNode.text);
	});
	
	var endtime = new Date().getTime();			
	console.log("Parse " + tplPath + " to response.(" + (endtime - starttime) + " milliseconds)");
	response.end();
}

exports.getOutput = function(buildObj){
	return buildObj;
}